def get_random_value_from_list(lst):
    return random.choice(lst)


class CustomLogger:
    def __init__(self, options=None):
        if options is None:
            options = {}
        self.debug = options.get('debug', True)

    def log(self, message):
        if self.debug:
            current_date_time = datetime.datetime.now().isoformat()
            random_emoji = get_random_value_from_list(emojis)
            print(f"[Starecase] : {current_date_time} - {message} {random_emoji}")
